const img=document.querySelectorAll(".gallery__img"),btn=document.querySelectorAll(".gallery__btn");let numberBtn;for(let a=0;a<btn.length;a++)btn[a].addEventListener("click",()=>{if(numberBtn=btn[a].dataset.number,btn[a].dataset.number==numberBtn){for(let a=0;a<img.length;a++)btn[a].dataset.number==numberBtn&&(img[a].style.display="none",btn[a].style.outline="none");img[a].style.display="block",btn[a].style.outline="2px solid #00accc"}});
